
import React, { createContext, useContext, useMemo, useRef, useEffect, useState } from 'react';
import { Settings } from '../types';

// Base64 encoded audio files to avoid needing external assets
const SNAP_SOUND = 'data:audio/wav;base64,UklGRiQSAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YQASAACAgID/AP8A/wD/AP8A/wD/AP8A/wAA';
const WIN_SOUND = 'data:audio/wav;base64,';
const CLICK_SOUND = 'data:audio/wav;base64,UklGRlIAAABXQVZFZm10IBAAAAABAAEARKwAAIhYAQACABAAZGF0YUgAAACAgICqqqr/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A/wD/AP8A';
const MUSIC_LOOP = 'data:audio/mp3;base64,ИА';

interface AudioContextType {
  playSnap: () => void;
  playWin: () => void;
  playClick: () => void;
}

const AudioContext = createContext<AudioContextType | null>(null);

export const AudioProvider: React.FC<{ children: React.ReactNode; settings: Settings }> = ({ children, settings }) => {
  const musicRef = useRef<HTMLAudioElement | null>(null);
  const [hasInteracted, setHasInteracted] = useState(false);

  // Detect first user interaction to unlock audio
  useEffect(() => {
    const handleInteraction = () => {
      setHasInteracted(true);
    };

    // Listen for any common interaction
    window.addEventListener('click', handleInteraction, { once: true });
    window.addEventListener('touchstart', handleInteraction, { once: true });
    window.addEventListener('keydown', handleInteraction, { once: true });

    return () => {
      window.removeEventListener('click', handleInteraction);
      window.removeEventListener('touchstart', handleInteraction);
      window.removeEventListener('keydown', handleInteraction);
    };
  }, []);

  // Handle Music Playback
  useEffect(() => {
    if (!musicRef.current) {
      musicRef.current = new Audio(MUSIC_LOOP);
      musicRef.current.loop = true;
    }

    const audio = musicRef.current;

    if (settings.music && hasInteracted) {
      const playPromise = audio.play();
      if (playPromise !== undefined) {
        playPromise.catch(error => {
          // This might happen if the interaction wasn't "trusted" enough or too much time passed
          console.warn("Autoplay prevented:", error);
        });
      }
    } else {
      audio.pause();
    }
  }, [settings.music, hasInteracted]);

  const playSound = (sound: string) => {
    if (settings.effects) {
      // Simple one-shot playback
      const audio = new Audio(sound);
      audio.play().catch(e => console.warn("Sound effect failed:", e));
    }
  };

  const audioControls = useMemo(() => ({
    playSnap: () => playSound(SNAP_SOUND),
    playWin: () => playSound(WIN_SOUND),
    playClick: () => playSound(CLICK_SOUND),
  }), [settings.effects]);

  return (
    <AudioContext.Provider value={audioControls}>
      {children}
    </AudioContext.Provider>
  );
};

export const useAudio = (): AudioContextType => {
  const context = useContext(AudioContext);
  if (!context) {
    throw new Error('useAudio must be used within an AudioProvider');
  }
  return context;
};
    